import React, { useState, useEffect } from 'react';
import {
  Container, Paper, Typography, Tabs, Tab, Table, TableBody, TableCell,
  TableHead, TableRow, Button, Dialog, DialogTitle, DialogContent,
  DialogActions, TextField, IconButton
} from '@mui/material';
import { Visibility as VisibilityIcon, Check as CheckIcon, Delete as DeleteIcon } from '@mui/icons-material';
import axios from '../utils/axios';

const AdminDashboard = () => {
  const [tabValue, setTabValue] = useState(0);
  const [pendingRequests, setPendingRequests] = useState([]);
  const [verifiedUsers, setVerifiedUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [pending, verified] = await Promise.all([
        axios.get('/api/admin/pending-requests'),
        axios.get('/api/admin/verified-jobholders')
      ]);
      setPendingRequests(pending.data);
      setVerifiedUsers(verified.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleVerify = async () => {
    try {
      await axios.post(`/api/admin/verify-jobholder/${selectedUser._id}`);
      setOpenDialog(false);
      fetchData();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>Admin Dashboard</Typography>
        <Tabs value={tabValue} onChange={(e, newValue) => setTabValue(newValue)}>
          <Tab label="Pending Requests" />
          <Tab label="Verified Job Holders" />
        </Tabs>

        {tabValue === 0 && (
          <Table>
            <TableHead>
              <TableRow><TableCell>Email</TableCell><TableCell>Actions</TableCell></TableRow>
            </TableHead>
            <TableBody>
              {pendingRequests.map((user) => (
                <TableRow key={user._id}>
                  <TableCell>{user.email}</TableCell>
                  <TableCell>
                    <Button onClick={() => { setSelectedUser(user); setOpenDialog(true); }} startIcon={<CheckIcon />}>Verify</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        )}

        <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
          <DialogTitle>Verify Jobholder</DialogTitle>
          <DialogActions>
            <Button onClick={handleVerify} variant="contained" color="primary">Verify</Button>
          </DialogActions>
        </Dialog>
      </Paper>
    </Container>
  );
};

export default AdminDashboard;
