import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Tabs,
  Tab,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions
} from '@mui/material';
import { Delete as DeleteIcon } from '@mui/icons-material';
import axios from 'axios';

const AdminDashboard = () => {
  const [value, setValue] = useState(0);
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);

  useEffect(() => {
    fetchData();
  }, [value]);

  const fetchData = async () => {
    try {
      if (value === 0) {
        const response = await axios.get('http://localhost:5000/api/users');
        setUsers(response.data);
      } else {
        const response = await axios.get('http://localhost:5000/api/jobs');
        setJobs(response.data);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleDelete = async () => {
    try {
      if (value === 0) {
        await axios.delete(`http://localhost:5000/api/users/${selectedItem._id}`);
        setUsers(users.filter(user => user._id !== selectedItem._id));
      } else {
        await axios.delete(`http://localhost:5000/api/jobs/${selectedItem._id}`);
        setJobs(jobs.filter(job => job._id !== selectedItem._id));
      }
      setOpenDialog(false);
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const handleTabChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Container sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Admin Dashboard
        </Typography>
        
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
          <Tabs value={value} onChange={handleTabChange}>
            <Tab label="Manage Users" />
            <Tab label="Manage Jobs" />
          </Tabs>
        </Box>

        <Box role="tabpanel" hidden={value !== 0}>
          {value === 0 && (
            <List>
              {users.map((user) => (
                <ListItem key={user._id}>
                  <ListItemText
                    primary={user.username}
                    secondary={`${user.email} - ${user.role} - ${user.college}`}
                  />
                  <ListItemSecondaryAction>
                    <IconButton
                      edge="end"
                      onClick={() => {
                        setSelectedItem(user);
                        setOpenDialog(true);
                      }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          )}
        </Box>

        <Box role="tabpanel" hidden={value !== 1}>
          {value === 1 && (
            <List>
              {jobs.map((job) => (
                <ListItem key={job._id}>
                  <ListItemText
                    primary={job.companyName}
                    secondary={`${job.role} - ${job.college}`}
                  />
                  <ListItemSecondaryAction>
                    <IconButton
                      edge="end"
                      onClick={() => {
                        setSelectedItem(job);
                        setOpenDialog(true);
                      }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          )}
        </Box>

        <Dialog
          open={openDialog}
          onClose={() => setOpenDialog(false)}
        >
          <DialogTitle>Confirm Delete</DialogTitle>
          <DialogContent>
            Are you sure you want to delete this {value === 0 ? 'user' : 'job'}?
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
            <Button onClick={handleDelete} color="error">Delete</Button>
          </DialogActions>
        </Dialog>
      </Paper>
    </Container>
  );
};

export default AdminDashboard;
