import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  InputAdornment,
  Paper
} from '@mui/material';
import { useAuth } from '../context/AuthContext';
import api from '../utils/axios';

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await api.get(`/api/jobs/college/${user.college}`);
        setJobs(response.data);
      } catch (error) {
        console.error('Error fetching jobs:', error);
      }
    };

    if (user?.college && user.role === 'user') {
      fetchJobs();
    }
  }, [user?.college, user?.role]);

  const JobHolderHome = () => (
    <Box sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 4, mb: 4, textAlign: 'center' }}>
        <Typography variant="h4" gutterBottom>
          Help Your Juniors Succeed
        </Typography>
        <Typography variant="body1" paragraph>
          Share your valuable experience and help guide the next generation of professionals.
        </Typography>
        <Button
          variant="contained"
          size="large"
          onClick={() => navigate('/jobholder/dashboard')}
          sx={{ mt: 2 }}
        >
          Open Dashboard
        </Button>
      </Paper>

      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h5" gutterBottom>
                Add Questions & Reviews
              </Typography>
              <Typography variant="body1" paragraph>
                Share interview questions and your interview experience to help others prepare better.
              </Typography>
              <Button
                variant="outlined"
                onClick={() => navigate('/jobholder/dashboard')}
              >
                Start Contributing
              </Button>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h5" gutterBottom>
                Post New Job Openings
              </Typography>
              <Typography variant="body1" paragraph>
                Let others know about opportunities at your company.
              </Typography>
              <Button
                variant="outlined"
                onClick={() => navigate('/jobholder/dashboard')}
              >
                Post a Job
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );

  const StudentHome = () => (
    <Box sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        Latest Jobs in {user?.college}
      </Typography>
      <Grid container spacing={4}>
        {jobs.map((job) => (
          <Grid item xs={12} md={6} lg={4} key={job._id}>
            <Card>
              <Box sx={{ display: 'flex', alignItems: 'center', p: 2 }}>
                {job.companyImage && (
                  <CardMedia
                    component="img"
                    sx={{ width: 50, height: 50, objectFit: 'contain', mr: 2 }}
                    image={`${process.env.REACT_APP_API_URL}/${job.companyImage}`}
                    alt={job.companyName}
                  />
                )}
                <Typography variant="h6">
                  {job.companyName}
                </Typography>
              </Box>
              <CardContent>
                <Typography variant="subtitle1" gutterBottom>
                  Role: {job.role}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Salary: â‚¹{job.salary}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Contact: {job.postedBy?.email}
                </Typography>
                <Box sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 1 }}>
                  <Button
                    variant="outlined"
                    onClick={() => navigate(`/job/${job._id}/questions`)}
                  >
                    Check Questions
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={() => navigate(`/job/${job._id}`)}
                  >
                    Check Description
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={() => navigate(`/job/${job._id}/reviews`)}
                  >
                    Check Reviews
                  </Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );

  return (
    <Container>
      {user?.role === 'jobholder' ? <JobHolderHome /> : <StudentHome />}
    </Container>
  );
};

export default Home;
