import React, { useState } from 'react';
import AddJob from './components/AddJob';
import Questions from './components/Questions';
import Jobs from './components/Jobs';

function App() {
  const [activeTab, setActiveTab] = useState('jobs');

  const handleDashboardClick = () => {
    setActiveTab('addJob');
  };

  const handleStartContributingClick = () => {
    setActiveTab('questions');
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="container mx-auto px-6 py-8">
        <nav className="bg-blue-50 rounded-lg p-4 mb-8">
          <div className="flex flex-col md:flex-row justify-between items-center gap-4">
            <div className="flex space-x-3 w-full md:w-auto">
              <button
                className={`px-6 py-2.5 rounded-md transition-all duration-200 font-medium text-sm
                  ${activeTab === 'jobs' 
                    ? 'bg-blue-500 text-white' 
                    : 'bg-white text-blue-600 hover:bg-blue-100'}`}
                onClick={() => setActiveTab('jobs')}
              >
                Jobs
              </button>
              <button
                className={`px-6 py-2.5 rounded-md transition-all duration-200 font-medium text-sm
                  ${activeTab === 'addJob'
                    ? 'bg-blue-500 text-white'
                    : 'bg-white text-blue-600 hover:bg-blue-100'}`}
                onClick={() => setActiveTab('addJob')}
              >
                Add Job
              </button>
              <button
                className={`px-6 py-2.5 rounded-md transition-all duration-200 font-medium text-sm
                  ${activeTab === 'questions'
                    ? 'bg-blue-500 text-white'
                    : 'bg-white text-blue-600 hover:bg-blue-100'}`}
                onClick={() => setActiveTab('questions')}
              >
                Questions
              </button>
            </div>
            <div className="flex space-x-3 w-full md:w-auto justify-center md:justify-end">
              <button
                className="px-6 py-2.5 rounded-md bg-blue-500 text-white font-medium 
                  text-sm hover:bg-blue-600 transition-all duration-200 min-w-[120px]"
                onClick={handleDashboardClick}
              >
                Dashboard
              </button>
              <button
                className="px-6 py-2.5 rounded-md bg-green-500 text-white font-medium 
                  text-sm hover:bg-green-600 transition-all duration-200 min-w-[120px]"
                onClick={handleStartContributingClick}
              >
                Start Contributing
              </button>
            </div>
          </div>
        </nav>

        <div className="bg-white border border-gray-200 rounded-lg p-6">
          {activeTab === 'jobs' && <Jobs />}
          {activeTab === 'addJob' && <AddJob />}
          {activeTab === 'questions' && <Questions />}
        </div>
      </div>
    </div>
  );
}

export default App;
