import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box
} from '@mui/material';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/jobs/college/${user.college}`);
        setJobs(response.data);
      } catch (error) {
        console.error('Error fetching jobs:', error);
      }
    };

    fetchJobs();
  }, [user.college]);

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Latest Jobs at {user.college}
      </Typography>
      <Grid container spacing={4}>
        {jobs.map((job) => (
          <Grid item xs={12} sm={6} md={4} key={job._id}>
            <Card>
              {job.companyImage && (
                <CardMedia
                  component="img"
                  height="140"
                  image={`http://localhost:5000/${job.companyImage}`}
                  alt={job.companyName}
                />
              )}
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {job.companyName}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Role: {job.role}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Salary: â‚¹{job.salary}
                </Typography>
                <Box sx={{ mt: 2 }}>
                  <Button 
                    size="small" 
                    onClick={() => navigate(`/job/${job._id}/questions`)}
                  >
                    Check Questions
                  </Button>
                  <Button 
                    size="small" 
                    onClick={() => navigate(`/job/${job._id}`)}
                  >
                    Check Description
                  </Button>
                  <Button 
                    size="small" 
                    onClick={() => navigate(`/job/${job._id}/reviews`)}
                  >
                    Check Reviews
                  </Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Home;
