import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Tabs,
  Tab,
  TextField,
  Button,
  MenuItem,
  Grid,
  Card,
  CardMedia,
  CardContent
} from '@mui/material';
import api from '../utils/axios';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const JobHolderDashboard = () => {
  const [value, setValue] = useState(0);
  const [jobForm, setJobForm] = useState({
    companyName: '',
    role: '',
    salary: '',
    description: '',
    companyImage: null
  });
  const [questionForm, setQuestionForm] = useState({
    type: 'technical',
    question: '',
    answer: ''
  });
  const [reviewForm, setReviewForm] = useState({
    rounds: [{ roundNumber: 1, experience: '' }],
    overallExperience: ''
  });
  const { user } = useAuth();
  const [selectedJobId, setSelectedJobId] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [myJobs, setMyJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch user's jobs when component mounts
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await api.get(`/api/jobs/college/${user.college}`);
        setJobs(response.data);
        if (response.data.length > 0) {
          setSelectedJobId(response.data[0]._id);
        }
      } catch (error) {
        console.error('Error fetching jobs:', error);
      }
    };
    fetchJobs();
  }, [user.college]);

  useEffect(() => {
    const fetchMyJobs = async () => {
      try {
        setLoading(true);
        const response = await api.get('/api/jobs/myjobs');
        console.log('My jobs response:', response.data);
        setMyJobs(response.data);
      } catch (error) {
        console.error('Error fetching my jobs:', error);
        setError('Failed to load your jobs. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchMyJobs();
  }, []);

  const handleTabChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleJobSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      Object.keys(jobForm).forEach(key => {
        formData.append(key, jobForm[key]);
      });
      formData.append('college', user.college);

      await api.post('/api/jobs', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setJobForm({
        companyName: '',
        role: '',
        salary: '',
        description: '',
        companyImage: null
      });
      alert('Job posted successfully!');
    } catch (error) {
      console.error('Error posting job:', error);
      alert('Error posting job');
    }
  };

  const handleQuestionSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/api/questions', {
        ...questionForm,
        jobId: selectedJobId
      });
      setQuestionForm({
        type: 'technical',
        question: '',
        answer: ''
      });
      alert('Question added successfully!');
    } catch (error) {
      console.error('Error adding question:', error);
      alert('Error adding question');
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/api/reviews', {
        ...reviewForm,
        jobId: selectedJobId
      });
      setReviewForm({
        rounds: [{ roundNumber: 1, experience: '' }],
        overallExperience: ''
      });
      alert('Review added successfully!');
    } catch (error) {
      console.error('Error adding review:', error);
      alert('Error adding review');
    }
  };

  if (loading) {
    return (
      <Container sx={{ py: 4 }}>
        <Typography>Loading your jobs...</Typography>
      </Container>
    );
  }

  if (error) {
    return (
      <Container sx={{ py: 4 }}>
        <Typography color="error">{error}</Typography>
      </Container>
    );
  }

  return (
    <Container sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          My Posted Jobs
        </Typography>

        {myJobs.length === 0 ? (
          <Typography>You haven't posted any jobs yet.</Typography>
        ) : (
          <Grid container spacing={3}>
            {myJobs.map((job) => (
              <Grid item xs={12} md={6} key={job._id}>
                <Card>
                  <Box sx={{ display: 'flex', alignItems: 'center', p: 2 }}>
                    {job.companyImage && (
                      <CardMedia
                        component="img"
                        sx={{ width: 50, height: 50, objectFit: 'contain', mr: 2 }}
                        image={`${process.env.REACT_APP_API_URL}/${job.companyImage}`}
                        alt={job.companyName}
                      />
                    )}
                    <Typography variant="h6">
                      {job.companyName}
                    </Typography>
                  </Box>
                  <CardContent>
                    <Typography variant="subtitle1" gutterBottom>
                      Role: {job.role}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" gutterBottom>
                      Salary: â‚¹{job.salary}
                    </Typography>
                    <Typography variant="body2" paragraph>
                      {job.description}
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 1 }}>
                      <Button
                        variant="outlined"
                        size="small"
                        onClick={() => navigate(`/job/${job._id}/questions`)}
                      >
                        View Questions
                      </Button>
                      <Button
                        variant="outlined"
                        size="small"
                        onClick={() => navigate(`/job/${job._id}/reviews`)}
                      >
                        View Reviews
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Paper>
    </Container>
  );
};

export default JobHolderDashboard;
